package javaexp.a04_vo;

public class Emp5 {
	private String ename;
	private int grade;
	private int sal;
	public Emp5() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Emp5(String ename, int grade, int sal) {
		super();
		this.ename = ename;
		this.grade = grade;
		this.sal = sal;
	}
	public String getEname() {
		return ename;
	}
	public void setEname(String ename) {
		this.ename = ename;
	}
	public int getGrade() {
		return grade;
	}
	public void setGrade(int grade) {
		this.grade = grade;
	}
	public int getSal() {
		return sal;
	}
	public void setSal(int sal) {
		this.sal = sal;
	}
	
}
